    //save a new ccustomer to DB
    private void jButtonSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSaveActionPerformed

        String firstName=jTextFieldFirstName.getText();
        String lastName=jTextFieldLastName.getText();
        String phone=jTextFieldPhone.getText();
        firstName=firstName.trim();// REMOVE empty characters
        lastName=lastName.trim();
        phone=phone.trim();

        presenter.insertNewCustomer(firstName, lastName, phone);

    }//GEN-LAST:event_jButtonSaveActionPerformed

    // add an order to a customer
    private void jButtonAddOrderToCustomerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddOrderToCustomerActionPerformed

        String orderID=jTextFieldOrderID.getText();
        orderID=orderID.trim();//remove empty characters

        String priceStr=jTextFieldPrice.getText();
        int price=Integer.parseInt(priceStr.trim());

        String shipper=jTextFieldShipper.getText();
        shipper=shipper.trim();

        int customerID=Integer.parseInt(jTextFieldCustomerID.getText());
        presenter.addOrderToCustomer(orderID, price, shipper,customerID);

    }//GEN-LAST:event_jButtonAddOrderToCustomerActionPerformed

