    /**
     * @param args the command line arguments
     */

     //define interface methods
     public void bind(PurchasePresenter p)
     {
		presenter=p;
	 }

     //display customers
	 public void displayCustomerEntry(Customers c)
	 {
		jTextFieldCustomerID.setText(""+c.getCustomerID());
		jTextFieldFirstName.setText(c.getFirstName());
		jTextFieldLastName.setText(c.getLastName());
		jTextFieldPhone.setText(c.getPhone());
	 }

     //display orders
	 public void displayOrderEntries(Orders o)
	 {
		jTextFieldOrderID.setText(o.getOrderID());
	    jTextFieldPrice.setText(""+o.getPrice());
		jTextFieldShipper.setText(o.getShipper());
	 }

     //display dat on th etext area
	 public void displayDataTextArea(String s)
	 {
		 jTextArea1.setText(s);
	 }

     //set browsing
	 public void setBrowsing(boolean flag)
	 {
		 jButtonNext.setEnabled(flag);
		 jButtonPrevious.setEnabled(flag);
     }

     //display index
     public void displayMaxAndCurrentIndex(int m, int c)
     {
		 jTextFieldMaxIndex.setText(""+m);
		 jTextFieldCurrentIndex.setText(""+(c+1));
	 }

     //set the latest customer ID
     public void setLatestCustomerID(int id)
     {
		 jTextFieldCustomerID.setText(""+id);
	 }

	 //set customer phone
     public void setCustomerPhone(String p)
     {
		 jTextFieldPhone.setText(p);
	 }

